import React, { useState } from 'react'
import '../../Components/EditProfile/EditProfile.css'
import '../../Components/DataProfile/DataProfile.css'

function Profile() {
    const [toEditUserInfo, setToEditUserInfo] = useState(false)

    const [profileData, setProfileData] = useState({
        displayedName: 'Profile Name',
        aboutMe: 'Fun Fact',
        favorite: 'Favorite Sports',
        caloriesGoal: '',
        timeGoal: ''
    })

    function handleChange(event) {
        const { name, value, type } = event.target
        setProfileData(prevFormData => ({
            ...prevFormData,
            [name]: value
        }))
    }

    function handleEditUserInfo(event) {
        setToEditUserInfo(prevState => !prevState)
    }

    function handleSubmit(event) {
        event.preventDefault()
        console.log(profileData)
    }

    return (
        <div className='profile'>
            <div>
                <img src='./Images/PROFILE.png' alt='profile-pic' className='pro-pic' />
            </div>

            {toEditUserInfo ?
                <form onSubmit={handleSubmit}>
                    <div className='data-profile-edit'>

                        <input
                            type='text'
                            className='editProfileUserName'
                            placeholder='Profile Name'
                            name='displayedName'
                            value={profileData.displayedName}
                            onChange={handleChange}
                        />

                        <div className='data-profile-user'>
                            <input
                                type='text'
                                className='editProfile'
                                placeholder='Fun Fact'
                                name='aboutMe'
                                value={profileData.aboutMe}
                                onChange={handleChange}
                            />
                        </div>

                        <div className='data-profile-user'>
                            <input
                                type='text'
                                className='editProfile'
                                placeholder='Favorite Sports'
                                name='favorite'
                                value={profileData.favorite}
                                onChange={handleChange}
                            />
                        </div>

                        <input
                            type='text'
                            className='editProfileUserName'
                            placeholder='Calories Goal'
                            name='caloriesGoal'
                            value={profileData.caloriesGoal}
                            onChange={handleChange}
                        />

                        <input
                            type='text'
                            className='editProfileUserName'
                            placeholder='Time Goal'
                            name='timeGoal'
                            value={profileData.timeGoal}
                            onChange={handleChange}
                        />

                    </div>
                </form>
                :
                <div className="data-profile">

                    {profileData.displayedName}

                    <div className="data-profile-user">
                        {profileData.aboutMe}
                        <br />

                    </div>

                    <div className="data-profile-user">
                        {profileData.favorite}
                        <br />

                    </div>

                </div>}

            <button type="button" className="edit-profile" onClick={handleEditUserInfo}>
                {toEditUserInfo ? 'Done' : 'Edit'}
            </button>

            <button type="button" className="edit-profile">
                Logout
            </button>





        </div>
    )

}

export default Profile;
